export default class SummerDate extends Date {
    static getDate(d?: number | string | Date): {
        year: number;
        month: string | number;
        day: string | number;
        hours: string | number;
        minutes: string | number;
        seconds: string | number;
    };
    static currentDate(): string;
    static form(d: number | string | Date): string;
    static format(f: string, d?: number | string | Date): string;
}
